frappe.web_form.after_load = () => {
    if (window.location.pathname.endsWith("/new") && frappe.session.user) {
        let current_path = window.location.href;
        window.location.href = current_path.replace("/new", "/" + frappe.session.user);
    }
}

frappe.web_form.after_load = () => {
    console.log("✅ Web form loaded");

    // Update ending date when joining date or plan changes
    frappe.web_form.on('joining_date', () => update_ending_date());
    frappe.web_form.on('plans', () => {
        update_ending_date();
        update_final_price();
    });

    // Locker change triggers locker description and final price update
    frappe.web_form.on('locker', () => {
        update_locker_description();
        update_final_price();
    });

    // 👉 Manually listen for child field input events (BMI + Calories)
    setTimeout(() => {
        const grid = frappe.web_form.fields_dict.metrics_update?.grid;
        if (!grid) {
            console.error("❌ Child table grid not found");
            return;
        }

        grid.wrapper.on('blur', 'input[data-fieldname="height"], input[data-fieldname="weight"], input[data-fieldname="calorie_intake"]', function () {
            const $row = $(this).closest('.grid-row');
            const row_name = $row.attr("data-name");

            if (!row_name) {
                console.warn("⚠️ Could not get row name");
                return;
            }

            const rows = frappe.web_form.get_value("metrics_update") || [];
            const row = rows.find(r => r.name === row_name);

            if (!row) {
                console.warn("⚠️ Could not find row with name:", row_name);
                return;
            }

            let height = parseFloat(row.height);
            let weight = parseFloat(row.weight);
            let calorie = parseFloat(row.calorie_intake);

            console.log("📝 Row:", row_name);
            console.log("➡️ Height:", height);
            console.log("➡️ Weight:", weight);
            console.log("➡️ Calorie Intake:", calorie);

            if (!height || !weight || !calorie || height === 0) {
                console.log("⚠️ Missing or invalid input(s) for BMI calculation");
                return;
            }

            let bmi = (weight / (height * height)) + (calorie / 1000);

            row.bmi = bmi.toFixed(2);
            console.log("✅ Calculated BMI (with calorie):", row.bmi);

            frappe.web_form.set_value("metrics_update", rows).then(() => {
                console.log("✅ BMI updated in the form");
            });
        });

        console.log("✅ Listeners for height, weight, and calorie_intake are attached");
    }, 500);

    // 🔄 Trainer Filtering Based on Specialization
    frappe.web_form.on('specialization', () => {
        const specialization = frappe.web_form.get_value("specialization");
        console.log("🔄 Specialization Selected:", specialization);

        if (!specialization) {
            console.warn("⚠️ No specialization selected");
            return;
        }

        frappe.call({
            method: "gym.gym.api.get_filtered_trainers",
            args: {
                doctype: "TrainerResgistration",
                txt: "",
                searchfield: "name1",
                start: 0,
                page_len: 20,
                filters: JSON.stringify({ specialization })
            },
            callback: function (r) {
                console.log("✅ API Response:", r.message);

                const trainerSelect = document.querySelector('select[data-fieldname="trainer"]');
                if (!trainerSelect) {
                    console.warn("Trainer select field not found");
                    return;
                }

                trainerSelect.innerHTML = "";

                if (r.message && r.message.length > 0) {
                    r.message.forEach(opt => {
                        const optionElem = document.createElement("option");
                        optionElem.value = opt.value || opt.name || opt.label || "";
                        optionElem.textContent = opt.label || opt.value || opt.name || "";
                        trainerSelect.appendChild(optionElem);
                    });
                } else {
                    const optionElem = document.createElement("option");
                    optionElem.value = "";
                    optionElem.textContent = "No trainers available";
                    trainerSelect.appendChild(optionElem);
                }

                trainerSelect.dispatchEvent(new Event('change'));
                console.log("✅ Trainer options updated dynamically");
            },
            error: function (err) {
                console.error("❌ API Error:", err);
            }
        });
    });

    // 🧪 Locker Field Debugging
    console.log("✨ Web Form Loaded — Locker Debug Test");

    const lockerField = frappe.web_form.fields_dict.locker;
    if (!lockerField) {
        console.error("❌ locker field not found in fields_dict");
    } else {
        console.log("✅ locker field found");

        const select = lockerField.input;
        if (select) {
            console.log("✅ locker input element found");
            select.addEventListener('change', () => {
                const value = frappe.web_form.get_value('locker');
                console.log("🧪 Locker changed — value:", value);
                update_locker_description();  // Call locker logic
                update_final_price();         // Also update final price here in case direct DOM change triggered
            });
        } else {
            console.error("❌ locker input element not found");
        }
    }
};

// 🗓 Auto-Update Ending Date Based on Plan
function update_ending_date() {
    let joining_date = frappe.web_form.get_value('joining_date');
    let plan = frappe.web_form.get_value('plans');

    if (!joining_date || !plan) return;

    let months = 0;
    if (plan.includes("1 Month")) months = 1;
    else if (plan.includes("3 Months")) months = 3;
    else if (plan.includes("1 Year")) months = 12;

    if (months > 0) {
        const end_date = frappe.datetime.add_months(joining_date, months);
        frappe.web_form.set_value('ending_date', frappe.datetime.obj_to_str(end_date));
    }
}

// 🧩 Update Locker Description
function update_locker_description() {
    let locker = frappe.web_form.get_value('locker');
    let locker_count = parseInt(locker) || 0;

    console.log("🧩 update_locker_description called with count:", locker_count);

    if (locker_count === 0) {
        frappe.web_form.set_value('locker_description', "");
    } else {
        let locker_numbers = [];
        for (let i = 0; i < locker_count; i++) {
            let rand_num = Math.floor(100 + Math.random() * 900);
            locker_numbers.push(`L${rand_num}`);
        }

        const description = "Assigned Lockers: " + locker_numbers.join(", ");
        frappe.web_form.set_value('locker_description', description);
        console.log("✅ Locker Description Updated:", description);
    }
}

// 💰 Calculate and update final price including plan and locker price
function update_final_price() {
    const plan = frappe.web_form.get_value('plans');
    const lockerCount = parseInt(frappe.web_form.get_value('locker')) || 0;

    // Fetch gym settings - assuming single doc "Gym Settings"
    frappe.call({
        method: 'frappe.client.get',
        args: {
            doctype: 'Gym Settings',
            name: 'Gym Settings'
        },
        callback: function(res) {
            if (!res.message) {
                console.error("❌ Gym Settings not found");
                return;
            }
            const settings = res.message;

            // Determine base price based on selected plan
            let basePrice = 0;
            if (plan.includes("1 Month")) basePrice = parseFloat(settings.default_price_1m);
            else if (plan.includes("3 Months")) basePrice = parseFloat(settings.default_price_3m);
            else if (plan.includes("1 Year")) basePrice = parseFloat(settings.default_price_1y);

            const lockerPrice = parseFloat(settings.locker_price_1m) || 0;
            const totalLockerPrice = lockerPrice * lockerCount;

            const finalPrice = basePrice + totalLockerPrice;

            frappe.web_form.set_value('final_price', finalPrice.toFixed(2));
            console.log(`✅ Final price updated: ${finalPrice.toFixed(2)}`);
        }
    });
}

frappe.web_form.on('upload_image', (field, value) => {
    if (value) {
        const img_url = window.location.origin + value;
        const preview = `<img src="${img_url}" style="max-width:200px; margin-top:10px;">`;

        // Insert preview below the field
        frappe.web_form.fields_dict.upload_image.$wrapper.append(preview);
    }
});

