frappe.web_form.after_load = () => {
    console.log("Web Form loaded");

    setTimeout(() => {
        const grid = frappe.web_form.fields_dict.table_qexv?.grid;

        if (!grid) {
            console.error("Child table grid not found");
            return;
        }

        console.log("Grid found, attaching input listeners");

        grid.wrapper.on(
            'input',
            'input[data-fieldname="height"], input[data-fieldname="weight"]',
            function () {
                const $row = $(this).closest('.grid-row');
                const row_name = $row.attr("data-name");

                if (!row_name) {
                    console.warn("Could not get row name");
                    return;
                }

                const heightInput = $row.find('input[data-fieldname="height"]').val();
                const weightInput = $row.find('input[data-fieldname="weight"]').val();
                const bmiInput = $row.find('input[data-fieldname="bmi"]');

                const height = parseFloat(heightInput);
                const weight = parseFloat(weightInput);

                if (!height || !weight || height <= 0) {
                    if (bmiInput) bmiInput.val('');
                    return;
                }

                const bmi = (weight * 10000) / (height * height);
                const bmiVal = bmi.toFixed(2);

                if (bmiInput) {
                    bmiInput.val(bmiVal);
                    bmiInput.trigger('input');
                }

                const rows = frappe.web_form.get_value("table_qexv") || [];
                const row = rows.find(r => r.name === row_name);
                if (row) {
                    row.bmi = bmiVal;
                    frappe.web_form.set_value("table_qexv", rows);
                    console.log(`BMI calculated: ${bmiVal}`);
                }
            }
        );
    }, 500);
};

