const levelMap = {
    "Beginner": "Begineer",     // typo preserved
    "Intermediate": "Intermediate",
    "Advance": "Advanced"
};

frappe.web_form.on('level', (field, value) => {
    console.log("[DEBUG] Selected level:", value);

    if (!value) {
        frappe.web_form.set_value('workout_plan', '');
        return;
    }

    const filter_level = levelMap[value] || value;

    frappe.call({
        method: 'frappe.client.get',
        args: {
            doctype: 'WorkoutPlan',
            name: null,  // we'll get name via get_list first
            filters: { level: filter_level }
        },
        callback: function (res) {
            if (res.message) {
                // If your WorkoutPlan has child table 'workouts' with day-wise info:
                let planDoc = res.message;

                // Example: Let's say planDoc.workouts is the child table array
                let saturdayWorkout = '';
                if (planDoc.workouts && planDoc.workouts.length > 0) {
                    const saturdayEntry = planDoc.workouts.find(w => w.day.toLowerCase() === 'saturday');
                    saturdayWorkout = saturdayEntry ? saturdayEntry.workout : 'No Saturday workout found.';
                } else {
                    saturdayWorkout = planDoc.workout_plan || 'No plan found.';
                }

                frappe.web_form.set_value('workout_plan', saturdayWorkout);
            } else {
                frappe.web_form.set_value('workout_plan', 'No plan found for this level.');
            }
        }
    });
});
